<template>
    <div class="flex gap-6 px-2 md:gap-0 align-items-center justify-content-between">
        <div class="flex gap-2 align-items-center justify-content-between">
            <Icon @click="$emit('toggleSidebar', true)" icon="tabler:menu-2" class="cursor-pointer text-blue-300 xl:hidden block" width="36" height="36" />
            <div class="bg-orange-50 border-round-xl py-2  px-3 flex align-items-center gap-2 w-full ">
                <svg width="36" height="36" viewBox="0 0 46 46" fill="none" xmlns="http://www.w3.org/2000/svg"
                    xmlns:xlink="http://www.w3.org/1999/xlink">
                    <rect x="0.5" y="0.5" width="45" height="45" rx="22.5" fill="url(#pattern0)" stroke="#D6DBDB" />
                    <defs>
                        <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                            <use xlink:href="#image0_0_2754" transform="scale(0.00202429)" />
                        </pattern>
                        <image id="image0_0_2754" width="494" height="494"
                            xlink:href="data:image/png;base64," />
                    </defs>
                </svg>
                <p class="m-0 text-sm lg:text-md text-400 font-medium"> Girls Can Code</p>
            </div>
        </div>

        <div class="flex gap-3 align-items-center">
            <button class="p-link" aria-label="notification">
                <Icon icon="fluent:alert-24-regular" width="28" height="28" />
            </button>

            <button @click="toggle($event, 'languageMenu')" aria-haspopup="true" aria-controls="profile_menu"
                class="hidden gap-2 md:flex p-link align-items-center">
                <Icon icon="fluent:translate-24-regular" class="text-500" width="28" height="28" />
                <span class="text-sm uppercase">{{ currentLanguage }}</span>
                <Icon icon="fluent:caret-down-24-filled" color="#000000" width="14" height="14" />
            </button>


            <button @click="toggle($event, 'profileMenu')" aria-haspopup="true" aria-controls="language_menu"
                class="flex gap-2 p-2 p-link align-items-center hover:underline  hover:bg-blue-100">
                <Avatar image="./images/profile-image.png" size="normal" shape="circle" />
                <span class="hidden text-sm md:block ">Beth Amarachi</span>
                <Icon class="hidden md:block" icon="fluent:caret-down-24-filled" color="#000000" width="14" height="14" />
            </button>
        </div>

        <!-- Language Menu -->
        <Menu ref="languageMenu" id="language_menu" :model="languageMenuItems" :popup="true">
            <template #submenuheader="{ item }">
                <span class="text-base font-normal">{{ item.label }}</span>
            </template>

            <template #item="{ item, props }">
                <a @click="changeLanguage(item.code)" class="flex justify-content-between align-items-center"
                    v-bind="props.action">
                    <span class="mr-2 text-sm">{{ item.label }}</span>
                    <Icon v-if="item.code === currentLanguage" icon="fluent:checkmark-circle-20-regular"
                        class="text-blue-400" width="20" height="20" />
                </a>
            </template>
        </Menu>

        <!-- Menu Item -->
        <Menu ref="profileMenu" id="profile_menu" :model="profileMenuItems" :popup="true">
            <template #submenuheader="{ item }">
                <span class="text-base font-normal">{{ item.label }}</span>
            </template>

            <template #item="{ item, props }">
                <a class="flex align-items-center" v-bind="props.action">
                    <Icon :icon="item.icon" :class="item.color" width="20" height="20" />
                    <span class="ml-2 text-sm">{{ item.label }}</span>

                </a>
            </template>
        </Menu>
    </div>
</template>

<script setup>
import { Icon } from "@iconify/vue";
import Button from "primevue/button";
import Avatar from "primevue/avatar";
import Menu from "primevue/menu";
import { ref } from "vue";


defineProps({
    visible: Boolean
})

const currentLanguage = ref('en')
const profileMenu = ref();
const profileMenuItems = ref([
    {
        label: 'Change...',
        items: [
            { label: 'Upload file', icon: 'fluent:image-multiple-24-regular', color: 'text-purple-700' },
            { label: 'Use Camera', icon: 'fluent:camera-24-regular', color: 'text-green-400' }
        ]
    }
]);

const languageMenu = ref();
const languageMenuItems = ref([
    { label: 'English', code: 'en', },
    { label: 'Français', code: 'fr', },
    { label: 'عربي', code: 'ar', }

]);

const toggle = (event, menuIdentifier) => {
    menuIdentifier === 'profileMenu' ? profileMenu.value.toggle(event) : languageMenu.value.toggle(event)
};

const changeLanguage = (language) => {
    currentLanguage.value = language
}
</script>

<style lang="css" scoped></style>
